import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function LeaveType() {
  const { type } = useParams(); // Get leave type from URL params
  const [leave, setLeave] = useState([]); // State to store leave data
  const [error, setError] = useState(''); // State to store error message

  useEffect(() => {
    const fetchLeave = async () => {
      const token = localStorage.getItem('token'); // Get the auth token from localStorage

      try {
        const response = await axios.get('http://localhost:5000/api/LeaveType', {
          headers: {
            Authorization: `Bearer ${token}`, // Include token in headers
            type: type, // Include leave type in headers
          },
        });

        setLeave(response.data.filteredLeaves); // Set the fetched data to state
        console.log(response.data); // Log the fetched data
      } catch (err) {
        console.error('Error fetching leave details:', err.response ? err.response.data : err);
        setError(err.response ? err.response.data.message : 'An error occurred'); // Set error message
      }
    };

    fetchLeave(); // Call the function
  }, [type]); // Dependency array with `type`

  return (
    <div>
      <h1>Leave Details ({type})</h1>

      {/* Display error message if there's an error */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display leave details */}
      {leave.length > 0 ? (
        <ul>
          {leave.map(student => (
            <li key={student.studentId}>
              <strong>Name:</strong> {student.name} <br />
              <strong>Roll No:</strong> {student.rollno} <br />
              <strong>Leaves:</strong>
              <ul>
                {student.leaves.map(leave => (
                  <li key={leave._id}>
                    <strong>Type:</strong> {leave.leavetype} <br />
                    <strong>From:</strong> {new Date(leave.from).toLocaleDateString()} <br />
                    <strong>To:</strong> {new Date(leave.to).toLocaleDateString()} <br />
                    <strong>Description:</strong> {leave.description} <br />
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      ) : (
        !error && <p>No leave details found for the selected type.</p>
      )}
    </div>
  );
}

export default LeaveType;
