import React, { useState } from 'react'
import {useParams} from 'react-router-dom'

function LeaveType() {
    const {type} = useParams();
    const [leave,setLeave] = useState([]);
    const [error,setError] = useState('');
    useEffect(() => {
        const fetchStudents = async () => {
          const token = localStorage.getItem('token'); // Get the auth token
    
          try {
            const response = await axios.get('http://localhost:5000/api/LeaveType', {
              headers: {
                Authorization: `Bearer ${token}`, 
                type:type
              },
            });
            setLeave(response.data.filteredLeaves); // Store fetched students
          } catch (err) {
            console.error('Error fetching students:', err.response ? err.response.data : err);
            setError(err.response ? err.response.data.error : 'An error occurred'); // Store error message
          }
        };
    
        fetchStudents(); // Call the fetch function
      }, []); // Empty dependency array ensures this runs only once
    
