import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function AddLeave() {
  const [leavetype, setLeavetype] = useState('');
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [description, setDescription] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!leavetype || !from || !to) {
      setErrorMessage('All fields are required.');
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/addLeave',
        { leavetype, from, to, description },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSuccessMessage('Leave applied successfully!');
      alert("Leave applied successfully!");
      navigate('/profile');
    } catch (err) {
      console.error('Error applying leave:', err.response?.data || err.message);
      setErrorMessage(err.response?.data?.error || 'Failed to apply leave.');
      setSuccessMessage('');
    }
  };

  return (
    <div className="container mt-4">
      <h1 className="text-center mb-4">Apply for Leave</h1>
      <form onSubmit={handleSubmit} className="form-group">
        <div className="mb-3">
          <label htmlFor="leavetype" className="form-label">Leave Type</label>
          <select
            id="leavetype"
            className="form-select"
            value={leavetype}
            onChange={(e) => setLeavetype(e.target.value)}
            required
          >
            <option value="">Select Leave Type</option>
            <option value="Casual Leave">Casual Leave</option>
            <option value="Sick Leave">Sick Leave</option>
            <option value="Annual Leave">Annual Leave</option>
            <option value="Maternity Leave">Maternity Leave</option>
            <option value="Paternity Leave">Paternity Leave</option>
            <option value="Study Leave">Study Leave</option>
            <option value="Bereavement Leave">Bereavement Leave</option>
          </select>
        </div>

        <div className="mb-3">
          <label htmlFor="from" className="form-label">From</label>
          <input
            type="date"
            id="from"
            className="form-control"
            value={from}
            onChange={(e) => setFrom(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="to" className="form-label">To</label>
          <input
            type="date"
            id="to"
            className="form-control"
            value={to}
            onChange={(e) => setTo(e.target.value)}
            required
          />
        </div>

        <div className="mb-3">
          <label htmlFor="description" className="form-label">Description</label>
          <textarea
            id="description"
            className="form-control"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Enter the reason for leave..."
            rows="4"
            required
          ></textarea>
        </div>

        {errorMessage && <p className="text-danger">{errorMessage}</p>}
        {successMessage && <p className="text-success">{successMessage}</p>}

        <div className="d-grid gap-2">
          <button type="submit" className="btn btn-primary">Submit</button>
        </div>
      </form>
    </div>
  );
}

export default AddLeave;
