import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../css/AllStudents.css'; // Ensure the CSS file is imported

function AllStudents() {
  const [students, setStudents] = useState([]); // State to store students' data
  const [error, setError] = useState(null); // State to store errors
  const navigate = useNavigate(); // React Router hook for navigation

  // Fetch all students on component mount
  useEffect(() => {
    const fetchStudents = async () => {
      const token = localStorage.getItem('token'); // Get the auth token

      try {
        const response = await axios.get('http://localhost:5000/api/allStudents', {
          headers: {
            Authorization: `Bearer ${token}`, // Set the auth header
          },
        });
        setStudents(response.data.allStudent); // Store fetched students
      } catch (err) {
        console.error('Error fetching students:', err.response ? err.response.data : err);
        setError(err.response ? err.response.data.error : 'An error occurred'); // Store error message
      }
    };

    fetchStudents(); // Call the fetch function
  }, []); // Empty dependency array ensures this runs only once

  // Navigate to the profile page
  const handleProfile = (id) => {
    navigate(`/Profile/${id}`);
  };

  
  const handleEdit = (id) => {
    navigate(`/EditProfile/${id}`);
  };

  const handleDelete = (id) =>{
    navigate(`DeleteProfile`)
  }

  // JSX to render the component
  return (
    <div>
      <h1>All Students</h1>
      {error && <p>{error}</p>} {/* Show error if exists */}
      {students.length === 0 ? (
        <p>No students found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Department</th>
              <th>Room No</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student._id}>
                <td>
                  <img
                    src={`http://localhost:5000/Images/${student.ProfileImage}`}
                    alt={`${student.name}`}
                  />
                </td>
                <td>{student.name}</td>
                <td>{student.dept}</td>
                <td>{student.roomno}</td>
                <td>
                  <button onClick={() => handleProfile(student._id)}>View</button>
                  <button onClick={() => handleEdit(student._id)}>Edit</button>
                  <button>Leave</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default AllStudents;
