import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function LeaveView() {
  const { id } = useParams(); // Extract leave ID from the route parameters
  const [result, setResult] = useState(null); // State to store fetched data
  const [error, setError] = useState(''); // State to store error message

  useEffect(() => {
    const fetchLeaveDetails = async () => {
      const token = localStorage.getItem('token'); // Get the auth token

      try {
        const response = await axios.get(`http://localhost:5000/api/LeaveUpdate/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`, // Set the auth header
          },
        });

        console.log(response.data); // Log the response data for debugging
        setResult(response.data.result); // Store the result from the response
      } catch (err) {
        console.error('Error fetching leave details:', err.response ? err.response.data : err);
        setError(err.response ? err.response.data.error : 'An error occurred'); // Set the error message
      }
    };

    fetchLeaveDetails(); // Call the fetch function
  }, [id]); // Dependency array includes `id`

  return (
    <div>
      <h1>Leave Details</h1>

      {/* Display error if present */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display the fetched leave details */}
      {result ? (
        <div className="leave-details">
          <h2>Student Information</h2>
          <p><strong>Name:</strong> {result.name}</p>
          <p><strong>Roll No:</strong> {result.rollno}</p>
          <p><strong>Year:</strong> {result.year}</p>
          <img 
            src={`http://localhost:5000/uploads/${result.profileImage}`} 
            alt="Student Profile" 
            style={{ width: '150px', height: '150px', borderRadius: '50%' }} 
          />
          <h2>Leave Details</h2>
          <p><strong>Leave Type:</strong> {result.leaveDetails.leavetype}</p>
          <p><strong>From:</strong> {new Date(result.leaveDetails.from).toLocaleDateString()}</p>
          <p><strong>To:</strong> {new Date(result.leaveDetails.to).toLocaleDateString()}</p>
          <p><strong>Status:</strong> {result.leaveDetails.status}</p>
          <p><strong>Applied Date:</strong> {new Date(result.leaveDetails.appliedDate).toLocaleDateString()}</p>
          <p><strong>Description:</strong> {result.leaveDetails.description}</p>
        </div>
      ) : (
        !error && <p>Loading leave details...</p>
      )}
    </div>
  );
}

export default LeaveView;
