import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {useNavigate} from 'react-router-dom'

function AllStudents() {
  const [students,setStudents] = useState([]);
  const [error,SetError] = useState(null);
  const navigate = useNavigate();

  useEffect(()=>{
    const fetchStudents = async () =>{
      const token = localStorage.getItem('token');

      try{
        const response = await axios.get("http://localhost:5000/api/allStudents",{
          headers:{
            'Authorization':`Bearer ${token}`
          },
        });
        setStudents(response.data.allStudent);
      }
      catch(err){
        console.error("Error fetching students:", err.response ? err.response.data : err);
        setError(err.response ? err.response.data.error : "An error occurred");
      }
    };
    fetchStudents();
  },[]);

  const handleProfile = (id)=>{
    navigate(`/Profile/$`)
  }
  return (
    <div>
      <h1>All Students</h1>
      {error && <p>{error}</p>}
      {students.length === 0 ? (
        <p>No students found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Department</th>
              <th>Room No</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students.map((student) => (
              <tr key={student._id}>
                <td>
                  <img src={`http://localhost:5000/Images/${student.ProfileImage}`} alt={`${student.name}`}/>
                </td>
                <td>{student.name}</td>
                <td>{student.dept}</td>
                <td>{student.roomno}</td>
                <td>
                  <button onClick={() => handleProfile(student._id)}>View</button>
                  <button>Edit</button>
                  <button>Leave</button>
                </td>
              </tr>
            ))}
          </tbody>
          </table>
        )}
    </div>
  )
}

export default AllStudents
