import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min.js';

function AddLeave() {
  const [leavetype, setLeavetype] = useState('');
  const [from, setFrom] = useState('');
  const [to, setTo] = useState('');
  const [description, setDescription] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [leaveDays, setLeaveDays] = useState(0); // To display the calculated days
  const navigate = useNavigate();

  const calculateDays = (fromDate, toDate) => {
    const fromDateObj = new Date(fromDate);
    const toDateObj = new Date(toDate);

    // Calculate difference in milliseconds
    const differenceInTime = toDateObj - fromDateObj;

    // Convert milliseconds to days
    const differenceInDays = differenceInTime / (1000 * 3600 * 24);

    return differenceInDays;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Check if all fields are filled out
    if (!leavetype || !from || !to) {
      setErrorMessage('All fields are required.');
      return;
    }

    // Get today's date
    const today = new Date().toISOString().split('T')[0]; // Format to YYYY-MM-DD

    // Validate that the selected dates are not in the past
    if (from < today || to < today) {
      setErrorMessage('Leave dates cannot be in the past.');
      return;
    }

    // Validate that 'from' is not later than 'to'
    if (new Date(from) > new Date(to)) {
      setErrorMessage('The "From" date cannot be later than the "To" date.');
      return;
    }

    // Calculate the number of days
    const days = calculateDays(from, to);
    setLeaveDays(days);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/addLeave',
        { leavetype, from, to, description, days },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSuccessMessage('Leave applied successfully!');
      alert("Leave applied successfully!");
      navigate('/profile');
    } catch (err) {
      console.error('Error applying leave:', err.response?.data || err.message);
      setErrorMessage(err.response?.data?.error || 'Failed to apply leave.');
      setSuccessMessage('');
    }
  };

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }} className="d-flex justify-content-center align-items-center">
      <div className="card shadow p-4 w-100" style={{ maxWidth: '500px', borderRadius: '12px' }}>
        <h2 className="text-center text-primary mb-4">Apply for Leave</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="leavetype" className="form-label">Leave Type</label>
            <select
              id="leavetype"
              className="form-select"
              value={leavetype}
              onChange={(e) => setLeavetype(e.target.value)}
              required
            >
              <option value="">Select Leave Type</option>
              <option value="Casual Leave">Casual Leave</option>
              <option value="Sick Leave">Sick Leave</option>
              <option value="Annual Leave">Annual Leave</option>
              <option value="Maternity Leave">Maternity Leave</option>
              <option value="Paternity Leave">Paternity Leave</option>
              <option value="Study Leave">Study Leave</option>
              <option value="Bereavement Leave">Bereavement Leave</option>
            </select>
          </div>

          <div className="mb-3">
            <label htmlFor="from" className="form-label">From</label>
            <input
              type="date"
              id="from"
              className="form-control"
              value={from}
              onChange={(e) => setFrom(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="to" className="form-label">To</label>
            <input
              type="date"
              id="to"
              className="form-control"
              value={to}
              onChange={(e) => setTo(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="description" className="form-label">Description</label>
            <textarea
              id="description"
              className="form-control"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter the reason for leave..."
              rows="4"
              required
            ></textarea>
          </div>

          {errorMessage && <p className="text-danger text-center">{errorMessage}</p>}
          {successMessage && <p className="text-success text-center">{successMessage}</p>}

          <button type="submit" className="btn btn-primary w-100">Submit</button>
        </form>

        {leaveDays > 0 && (
          <div className="mt-3 text-center">
            <p><strong>Total Leave Days: {leaveDays} days</strong></p>
          </div>
        )}
      </div>
    </div>
  );
}

export default AddLeave;
