import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../css/StudProfile.css"; // Import the CSS file

function StudProfile() {
  const [student, setStudent] = useState(null); // Singular for a single student profile
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchStudent = async () => {
      const token = localStorage.getItem("token"); // Retrieve token from localStorage

      if (!token) {
        setError("Unauthorized: No token found");
        navigate("/login"); // Redirect to login if token is missing
        return;
      }

      try {
        const response = await axios.get("http://localhost:5000/api/studprofile", {
          headers: {
            Authorization: `Bearer ${token}`, // Attach token to request headers
          },
        });

        console.log(response.data.Stud); // Debugging response from the API
        setStudent(response.data.Stud); // Set student data from response
      } catch (err) {
        console.error("Error fetching student profile:", err.response ? err.response.data : err);
        setError(err.response?.data?.error || "An error occurred");
      }
    };

    fetchStudent();
  }, [navigate]); // Add `navigate` as a dependency

  return (
    <div className="pro-container">
      {error && <p className="error">{error}</p>}

      {student ? (
        <>
          <div className="right">
            <img
              src={`http://localhost:5000/Images/${student.ProfileImage}`}
              alt={`${student.name}'s profile`}
            />
          </div>
          <div className="left">
            <p>
                <strong>Name:</strong>{student.name}
            </p>
            <p>
              <strong>Department:</strong> {student.dept}
            </p>
            <p>
              <strong>Year:</strong> {student.year}
            </p>
            <p>
              <strong>Section:</strong> {student.sec}
            </p>
            <p>
              <strong>Email:</strong> {student.email}
            </p>
            <p>
              <strong>Roll No:</strong> {student.rollno}
            </p>
            <p>
              <strong>Room No:</strong> {student.roomno}
            </p>
            <p>
              <strong>Warden Name:</strong> {student.wardenname}
            </p>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}

export default StudProfile;
