import React from 'react';
import Header from './Header';
import Footer from './Footer';

const Layout = ({ children }) => {
  return (
    <>
      <Header />
      <main style={{ minHeight: 'calc(100vh - 300px)' }} className="d-flex flex-column">
        {children}
      </main>
      <Footer />
    </>
  );
};

export default Layout;
