import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import '../css/LeaveView.css'

function LeaveView() {
  const { id } = useParams();
  const [result, setResult] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchLeaveDetails = async () => {
      const token = localStorage.getItem('token');

      try {
        const response = await axios.get(`http://localhost:5000/api/LeaveUpdate/${id}`, {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        console.log(response.data);
        setResult(response.data.result);
      } catch (err) {
        console.error('Error fetching leave details:', err.response ? err.response.data : err);
        setError(err.response ? err.response.data.error : 'An error occurred');
      }
    };

    fetchLeaveDetails();
  }, [id]);

  return (
    <div className="leave-view">
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {result ? (
        <div className="leave-card">
          <div className="leave-card-image">
            <img
              src={`http://localhost:5000/Images/${result.profileImage}`}
              alt="Student Profile"
            />
          </div>
          <div className="leave-card-details">
            <p><strong>Name:</strong> {result.name}</p>
            <p><strong>Roll No:</strong> {result.rollno}</p>
            <p><strong>Year:</strong> {result.year}</p>
            <p><strong>Leave Type:</strong> {result.leaveDetails.leavetype}</p>
            <p><strong>From:</strong> {new Date(result.leaveDetails.from).toLocaleDateString()}</p>
            <p><strong>To:</strong> {new Date(result.leaveDetails.to).toLocaleDateString()}</p>
            <p><strong>Status:</strong> {result.leaveDetails.status}</p>
            <p><strong>Applied Date:</strong> {new Date(result.leaveDetails.appliedDate).toLocaleDateString()}</p>
            <p><strong>Description:</strong> {result.leaveDetails.description}</p>
            {result.leaveDetails.status=="Accepted"}
            <p><strong>Action:</strong><button className='Accept'>Accept</button> <button className='Reject'>Reject</button></p>
          </div>
        </div>
      ) : (
        !error && <p>Loading leave details...</p>
      )}
    </div>
  );
}

export default LeaveView;
