import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


function AddLeave() {
    const [leavetype, setLeavetype] = useState('');
    const [from, setFrom] = useState('');
    const [to, setTo] = useState('');
    const [description,setDescription] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!leavetype || !from || !to) {
            setErrorMessage('All fields are required.');
            return;
        }

        try {
            const token = localStorage.getItem('token');
            const response = await axios.post(
                'http://localhost:5000/api/addLeave',
                { leavetype, from, to,description },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            setSuccessMessage('Leave applied successfully!');
            alert("Leave applied successfully!")
           navi

        } catch (err) {
            console.error('Error applying leave:', err.response?.data || err.message);
            setErrorMessage(err.response?.data?.error || 'Failed to apply leave.');
            setSuccessMessage('');
        }
    };

    return (
        <div>
            <h1>Apply for Leave</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Leave Type:
                    <select value={leavetype} onChange={(e) => setLeavetype(e.target.value)}>
                        <option value="">Select Leave Type</option>
                        <option value="Casual Leave">Casual Leave</option>
                        <option value="Sick Leave">Sick Leave</option>
                        <option value="Annual Leave">Annual Leave</option>
                        <option value="Maternity Leave">Maternity Leave</option>
                        <option value="Paternity Leave">Paternity Leave</option>
                        <option value="Study Leave">Study Leave</option>
                        <option value="Bereavement Leave">Bereavement Leave</option>
                    </select>
                </label>
                <br />
                <label>
                    From:
                    <input
                        type="date"
                        value={from}
                        onChange={(e) => setFrom(e.target.value)}
                        required
                    />
                </label>
                <br />
                <label>
                    To:
                    <input
                        type="date"
                        value={to}
                        onChange={(e) => setTo(e.target.value)}
                        required
                    />
                </label>
                <br />
                <label>
                    Description:
                    <textarea
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                        placeholder="Enter the reason for leave..."
                        rows="4"
                        cols="50"
                        required
                    ></textarea>
                </label>
                <br />
                {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default AddLeave;
