import React from 'react'


function StudLogin() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState('');
    const [errorMessage, setErrorMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Form submitted with:', { email, password });

        try {
            const response = await axios.post('http://localhost:5000/api/StudentLogin', {
                email,
                password,
            });
            console.log('Response from API:', response.data);

            if (response.data.token) {
                setToken(response.data.token);
                localStorage.setItem('token', response.data.token);
                console.log('Token saved:', response.data.token);
                navigate('/profile'); 
            } else {
                setErrorMessage('Unexpected response from the server');
            }
        } catch (err) {
            console.error('Error during login:', err.response?.data || err.message);
            setErrorMessage(err.response?.data?.message || 'Login failed. Please try again.');
        }
    };

    return (
        <div style={{ maxWidth: '400px', margin: '0 auto', padding: '1em' }}>
            <h2>Admin Login</h2>
            <form onSubmit={handleSubmit}>
                <div style={{ marginBottom: '1em' }}>
                    <label>Email</label>
                    <input
                        type="text"
                        placeholder="Enter your email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                        style={{ width: '100%', padding: '0.5em', margin: '0.5em 0' }}
                    />
                </div>
                <div style={{ marginBottom: '1em' }}>
                    <label>Password</label>
                    <input
                        type="password"
                        placeholder="Enter your password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        style={{ width: '100%', padding: '0.5em', margin: '0.5em 0' }}
                    />
                </div>
                {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
                <button type="submit" style={{ padding: '0.5em 1em' }}>
                    Login
                </button>
            </form>
        </div>
    );
}

export default StudLogin
