import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Leaves() {
  const { id } = useParams(); 
  const [leaves, setLeaves] = useState([]); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null); 

  
  useEffect(() => {
    const fetchLeaves = async () => {
        const token = localStorage.getItem('token');
      try {
        const response = await axios.get(`http://localhost:5000/api/Leaves/${id}`, {
            headers: {
              Authorization: `Bearer ${token}`, 
            },
          });
        setLeaves(response.data.leaves); 
        setLoading(false); // Stop loading
      } catch (err) {
        setError(err.response?.data?.error || 'Failed to fetch data'); // Handle errors
        setLoading(false);
      }
    };

    fetchLeaves();
  }, [id]);

  // Handle loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Handle error state
  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <h1>Leave Details</h1>
      <div className="leave-container">
        <table>
          <thead>
            <tr>
              <th>SNo</th>
              <th>Leave Type</th>
              <th>From Date</th>
              <th>To Date</th>
              <th>Applied Date</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {leaves.map((leave, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{leave.leavetype}</td>
                <td>{new Date(leave.from)}</td>
                <td>{new Date(leave.to)}</td>
                <td>{leave.appliedDate}</td>
                <td>{leave.status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Leaves;
