import React, { useState } from 'react'
import { useParams } from 'react-router-dom'

function LeaveView() {
    const {id} = useParams();
    const [result,setResult] = useState(null);
    useEffect(() => {
        const fetchStudents = async () => {
          const token = localStorage.getItem('token'); // Get the auth token
    
          try {
            const response = await axios.get(`http://localhost:5000/api/LeaveUpdate/${id}`, {
              headers: {
                Authorization: `Bearer ${token}`, // Set the auth header
              },
            });
            console(response.data);
            setResult(response.data.allStudent); // Store fetched students
          } catch (err) {
            console.error('Error fetching students:', err.response ? err.response.data : err);
            setError(err.response ? err.response.data.error : 'An error occurred'); // Store error message
          }
        };
    
        fetchStudents(); // Call the fetch function
      }, []);
  return (
    <h1>{id}</h1>
  )
}

export default LeaveView
