import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Profile() {
    const { id } = useParams();
    const [details, setDetails] = useState(null); 
    useEffect(() => {
        const handleProfile = async () => {
            const token = localStorage.getItem('token');
            try {
                const response = await axios.get(`http://localhost:5000/api/profile/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setDetails(response.data.Details || {}); 
            } catch (err) {
                console.error('Error fetching profile details:', err);
            }
        };

        handleProfile();
    }, [id]);

    return (
        <div>
            {details ? (
                <div>
                    <img src={`http://localhost:5000/Images/${details.ProfileImage}`} alt={`${details.name}'s profile`} />
                    <p>Name: {details.name}</p>
                    <p>Department: {details.dept}</p>
                    <p>Year: {details.year}</p>
                    <p>Section: {details.sec}</p>
                    <p>Email: {details.email}</p>
                    <p>RollNo: {details.rollno}</p>
                    <p>RoomNo: {details.roomno}</p>
                    <p>Warden Name: {details.wardenname}</p>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
}

export default Profile;
