import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function EditProfile() {
    const { id } = useParams();
    const navigate = useNavigate();
    const [profileData, setProfileData] = useState({
        name: '',
        dept: '',
        year: '',
        sec: '',
        email: '',
        rollno: '',
        roomno: '',
        wardenname: '',
    });
    const [profileImage, setProfileImage] = useState(null);

    useEffect(() => {
        const fetchProfile = async () => {
            const token = localStorage.getItem('token');
            try {
                const response = await axios.get(`http://localhost:5000/api/profile/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setProfileData(response.data.Details);
            } catch (error) {
                console.error('Error fetching profile details:', error);
            }
        };

        fetchProfile();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProfileData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleImageChange = (e) => {
        setProfileImage(e.target.files[0]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        const formData = new FormData();
        formData.append('profileImage', profileImage);
        Object.keys(profileData).forEach((key) => {
            formData.append(key, profileData[key]);
        });

        try {
            await axios.put(`http://localhost:5000/api/profile/${id}`, formData, {
                headers: {
                    Authorization: `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert('Profile updated successfully!');
            navigate(`/profile/${id}`);
        } catch (error) {
            console.error('Error updating profile:', error);
            alert('Failed to update profile.');
        }
    };

    return (
        <div>
            <h2>Edit Profile</h2>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
                <label>
                    Name:
                    <input
                        type="text"
                        name="name"
                        value={profileData.name}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Department:
                    <input
                        type="text"
                        name="dept"
                        value={profileData.dept}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Year:
                    <input
                        type="text"
                        name="year"
                        value={profileData.year}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Section:
                    <input
                        type="text"
                        name="sec"
                        value={profileData.sec}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Email:
                    <input
                        type="email"
                        name="email"
                        value={profileData.email}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Roll No:
                    <input
                        type="text"
                        name="rollno"
                        value={profileData.rollno}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Room No:
                    <input
                        type="text"
                        name="roomno"
                        value={profileData.roomno}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Warden Name:
                    <input
                        type="text"
                        name="wardenname"
                        value={profileData.wardenname}
                        onChange={handleChange}
                    />
                </label>
                <label>
                    Profile Image:
                    <input
                        type="file"
                        name="profileImage"
                        onChange={handleImageChange}
                    />
                </label>
                <button type="submit">Save Changes</button>
            </form>
        </div>
    );
}

export default EditProfile;
