import React, { useState } from 'react'
import axios from 'axios'



function AddStudent() {
    const [formData,setFormData] = useState({
        name: '',
        year: '',
        dept: '',
        sec: '',
        email: '',
        rollno: '',
        roomno: '',
        wardenname: '',
        password: '',
        ProfileImage: null
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === "ProfileImage") {
            setFormData({ ...formData, [name]: e.target.files[0] });
        } else {
            setFormData({ ...formData, [name]: value });
        }
    };


    const handleSubmit = async (e) => {
        e.preventDefault();
        
        const token = localStorage.getItem('token'); // Get the token from local storage or state

        const data = new FormData();
        for (const key in formData) {
            data.append(key, formData[key]);
        }

        try {
            const response = await axios.post('http://localhost:5000/api/addStudent', data, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                    'Content-Type': 'multipart/form-data',
                },
            });
            alert(response.data.message);
        } catch (error) {
            console.error("Error adding student:", error.response.data);
            alert(error.response.data.error || "An error occurred");
        }
    };

    useEffect(() => {
        const fetchUsername = async () => {
          const token = localStorage.getItem('token'); // Get the auth token
    
          try {
            const response = await axios.get('http://localhost:5000/api/allStudents', {
              headers: {
                Authorization: `Bearer ${token}`, // Set the auth header
              },
            });
            setStudents(response.data.allStudent); // Store fetched students
          } catch (err) {
            console.error('Error fetching students:', err.response ? err.response.data : err);
            setError(err.response ? err.response.data.error : 'An error occurred'); // Store error message
          }
        };
    
        fetchStudents(); // Call the fetch function
      }, []);

    

  return (
    <div>
       <form onSubmit={handleSubmit}>
            <input type="text" name="name" placeholder="Name" onChange={handleChange} required />
            <input type="text" name="year" placeholder="Year" onChange={handleChange} required />
            <input type="text" name="dept" placeholder="Department" onChange={handleChange} required />
            <input type="text" name="sec" placeholder="Section" onChange={handleChange} required />
            <input type="email" name="email" placeholder="Email" onChange={handleChange} required />
            <input type="text" name="rollno" placeholder="Roll No" onChange={handleChange} required />
            <input type="text" name="roomno" placeholder="Room No" onChange={handleChange} required />
            <input type="text" name="wardenname" placeholder="Warden Name" onChange={handleChange} required />
            <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
            <input type="file" name="ProfileImage" accept="image/*" onChange={handleChange} required />
            <button type="submit">Add Student</button>
        </form>
    </div>
  )
}

export default AddStudent
