import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function Profile() {
    const { id } = useParams();
    const [details, setDetails] = useState([]); // Start as an empty array

    useEffect(() => {
        const handleProfile = async () => {
            const token = localStorage.getItem('token');
            try {
                const response = await axios.get(`http://localhost:5000/api/profile/${id}`, {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                });
                setDetails(response.data.Details || []); // Ensure response data is an array
            } catch (err) {
                console.error('Error fetching profile details:', err);
            }
        };

        handleProfile();
    }, [id]); // Dependency array with id

    return (
        <div>
            {details && details.length > 0 ? ( // Check that details is an array and has content
                details.map((detail, index) => (
                    <div key={index}>
                        <img src={`http://localhost:5000/Images/${detail.ProfileImage}`} alt={`${detail.name}'s profile`} />
                        <p>Name: {detail.name}</p>
                        <p>Department: {detail.dept}</p>
                        <p>Year: {detail.year}</p>
                        <p>Section: {detail.sec}</p>
                        <p>Email: {detail.email}</p>
                        <p>RollNo: {detail.rollno}</p>
                        <p>RoomNo: {detail.roomno}</p>
                        <p>Warden Name: {detail.wardenname}</p>
                    </div>
                ))
            ) : (
                <p>Loading...</p> // Loading message while details are being fetched
            )}
        </div>
    );
}

export default Profile;
